require_relative "mew"

data = Mew.data.first

announce_part_one
data.split('')
  .each_cons(4).with_index {|chunk, index| chunk.uniq.sort == chunk.sort and break puts index + 4 }

announce_part_two
data.split('')
  .each_cons(14).with_index {|chunk, index| chunk.uniq.sort == chunk.sort and break puts index + 14 }

__END__

